import React, { Component } from "react";
import { ProductConsumer } from "./../../context";
import { NavLink } from "react-router-dom";
import classes from "./Details.css";
export default class Details extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const {
            id,
            title,
            company,
            img,
            info,
            price,
            inCart
          } = value.detailProduct;
          return (
            <div className={classes.Container}>
              <div className={classes.ImgContainer}>
                <img src={img} alt={title} className={classes.Img} />
              </div>
              <div className={classes.InfoContainer}>
                <h1 className={classes.Title}>{title}</h1>
                <h3>Made by: {company}</h3>
                <p>
                  <strong>Description: </strong>
                  {info}
                </p>
                <h2>Price: Â£ {price}</h2>
                <div className={classes.BtnContainer}>
                  <NavLink to="/shop">
                    <button className={classes.BackButton}>Back to Shop</button>
                  </NavLink>
                  <button
                    disabled={inCart ? true : false}
                    onClick={() => {
                      value.addToCart(id);
                      value.openModal(id);
                    }}
                    className={
                      inCart ? classes.CartBtnDisable : classes.CartBtn
                    }
                  >
                    {inCart ? <span>in Cart</span> : <span>ADD To CART</span>}
                  </button>
                </div>
              </div>
            </div>
          );
        }}
      </ProductConsumer>
    );
  }
}
